import SessionStorage from '../services/SessionStorage';
export const saveSession = (key,{access_token, refresh_token,user}, withUser = true) => {
    const options = {
        path: '/',
        expires: new Date( Date.now()),
    };
    SessionStorage.set('access_token', access_token, options);
    SessionStorage.set('refresh_token', refresh_token, options);
    if (withUser) {
        SessionStorage.set('user', user, options);
    }
};

export const getSession = (key) => {
    let result = SessionStorage.get(key);

    return result;
}

export const destroySession = () => {
    const options = {
        path: '/'
    };
    SessionStorage.remove('access_token', options);
    SessionStorage.remove('refresh_token', options);
    SessionStorage.remove('user', options);
};
