import React  from "react";
import {connect} from "react-redux";

class LocalizeTab extends React.Component {
    render() {
        console.log(this.props)
        return (
            <div>
                {this.props.languages.map(function (lang) {

                    return <input
                        className={`form-control  ${this.props.errors ? 'is-invalid' : ''} ${lang.code !== this.props.modalLang ? 'd-none' : ''}`}
                        type="text"
                        value={this.props.name && this.props.name[this.props.modalLang] ? this.props.name[this.props.modalLang] : ""}
                        onChange={event => this.props.setValue("name", event.target.value, this.props.modalLang)}

                    />
                })}
            </div>
        );
    }
}


const mapStateToProps = state => {
    return {
        languages: state.languages.languages,
        modalLang:state.languages.modalLang,
    }
};

export default connect(mapStateToProps)(LocalizeTab);
