import React, { useState} from "react";
import {Row, Col} from "reactstrap";
import { Minus, Plus,Check} from "react-feather";
import PlusButton from "../../../components/buttons/plusButton";
import EditButton from "../../../components/buttons/editButton";
import DeleteButton from "../../../components/buttons/deleteButton";

function SubMenu(props) {
    console.log(props)
    const [open, setOpen] = useState(false);
    function handleOpen() {
        setOpen(!open)
    }

    function SubMenuItem(props) {
        if (open && props.data.child) {
            return <Menu
                data={props.data.child}
                toggleModal={props.toggleSubModal}
                toggleSubModal = {props.toggleSubModal}
                actions={props.actions}
                group_id = {props.group_id}
            />
        }
        return <div></div>
    }

    function Chevron(props) {
        if (props.data) {
            if (!open) {
                return <Plus size={18} className="mr-2" onClick={() => handleOpen()}/>
            }
            return <Minus size={18} className="mr-2" onClick={() => handleOpen()}/>
        }
        return <Check size={18} className="mr-2" onClick={() => handleOpen()}/>
    }
    return (
        <li className="list-group-item list-group-item-action border-0 my-0">
            <Row className="todo-list-group-item border  rounded">
                <Col sm="11" md="11">
                    <Chevron data={props.data.child}/>
                    {props.data.name}
                </Col>
                <Col sm="1" md="1">
                    <PlusButton perm={props.perm} onClick={() => props.toggleSubModal('add',props.data.id,props.group_id)}/>
                    <EditButton perm={props.perm} onClick={() => { props.toggleSubModal('edit',props.data.id,props.group_id); props.actions("get",props.data)}}/>
                    <DeleteButton perm={props.perm} onClick={() => props.toggleSubModal('delete',props.data.id,props.group_id)}/>
                </Col>
            </Row>
            <SubMenuItem {...props}/>
        </li>
    )
};

function Menu(props) {
    console.log(props.actions)
    if (props.data && props.data.length > 0) {
        return <ul className="list-group ">
            {props.data.map((value, index) => {
                return (
                    <SubMenu
                        key = {index}
                        data={value}
                        toggleSubModal = {props.toggleSubModal}
                        perm={props.perm}
                        actions={props.actions}
                        group_id = {props.group_id}
                    />
                )
            })}

        </ul>

    } else {
        return <div></div>
    }
}


export default Menu;
