// import external modules
import React, {Component} from "react";
import {BrowserRouter, Redirect, Route, Switch} from "react-router-dom";
import "react-perfect-scrollbar/dist/css/styles.css";
// import internal(own) modules
import {connect} from 'react-redux';
import Router12 from "./router";
import PublicRouter from "./publicRouter";
import history from "../history";


const PrivateRouter = ({ auth,component: Component, ...rest }) => (

    <Route {...rest} render={(props) => (
        auth
            ?
            <Redirect to='/index' />
            : <Redirect to='/login' />
    )} />
);
// const App = props => <Router />;
function App(props) {
    console.log(props.auth.authUser)
        return(
            <BrowserRouter basename="/">
                <Switch>
                    <PublicRouter/>
                    <PrivateRouter component={Router12} auth={props.auth.authUser}/>
                </Switch>
            </BrowserRouter>
        )
        if(props.auth.authUser){
            console.log("logddvgds")
            return( <Redirect to='/index' />)
        }else{
            console.log("login")
            return (<Redirect to='/login' />)
        }


}

function mapStateToProps(state) {
    return state;
}
export default connect(
    mapStateToProps,
)(App);
