import React, { Component } from "react";
import {
    Row,
    Col,
    Input,
    Form,
    FormGroup,
    Button,
    Label,
    Card,
    CardBody
} from "reactstrap";
import  jwt  from 'jwt-simple';
import { connect } from "react-redux";
import { bindActionCreators } from "redux";
import { login } from "../../redux/auth/actions";
import * as Yup from "yup";
import {Field, Formik} from "formik";


class LoginContainer extends Component {
    state = {
        rememberMe: false
    };
    formSchema = Yup.object().shape({
        username: Yup.string()
            .email("Invalid email")
            .required("Required"),
        password: Yup.number()
            .required("Required"),

    });
    handleChange = event => {
        this.setState({ ...this.state, [event.target.name]: event.target.value });
    };
    handleChecked = e => {
        this.setState({...this.state,rememberMe: !this.state.rememberMe});
    };
    // onSubmit = () => {
    //     let data = [this.state.username,jwt.encode(this.state.password, "password")];
    //     this.props.login(data,'index')
    // }

    render() {
        console.log("sdgsdg")
        return (
            <div className="container">
                <Row className="full-height-vh">
                    <Col xs="12" className="d-flex align-items-center justify-content-center">
                        <Card className="gradient-indigo-purple text-center width-400">
                            <CardBody>
                                <h2 className="white py-4">Login</h2>
                                <Formik
                                    initialValues={{
                                        required: "",
                                        email: "",
                                        url: "",
                                        number: "",
                                        date: "",
                                        minlength: "",
                                        maxlength: ""
                                    }}
                                    validationSchema={this.formSchema}
                                    onSubmit={values => {
                                        // same shape as initial values
                                        console.log(values);
                                    }}
                                >
                                    {({ errors, touched }) => (
                                <Form className="pt-2">
                                    <FormGroup>
                                        <Col md="12">
                                            <Field
                                                name="username"
                                                id="inputEmail"
                                                placeholder="Email"
                                                onChange={this.handleChange}
                                                className={`form-control ${errors.required && touched.required && 'is-invalid'}`}
                                            />
                                            {errors.required && touched.required ? <div className="invalid-feedback">{errors.required}</div> : null}

                                        </Col>
                                    </FormGroup>

                                    <FormGroup>
                                        <Col md="12">
                                            <Field
                                                type="password"
                                                name="password"
                                                id="inputPass"
                                                placeholder="Password"
                                                onChange={this.handleChange}
                                                className={`form-control ${errors.required && touched.required && 'is-invalid'}`}
                                            />
                                            {errors.required && touched.required ? <div className="invalid-feedback">{errors.required}</div> : null}
                                        </Col>
                                    </FormGroup>


                                    <FormGroup>
                                        <Col md="12">
                                            <Button  color="danger" block className="btn-pink btn-raised" onClick={this.onSubmit}>
                                                Submit
                                            </Button>
                                        </Col>
                                    </FormGroup>
                                </Form>
                                    )}
                                </Formik>
                            </CardBody>
                        </Card>
                    </Col>
                </Row>
            </div>
        );
    }
}

function mapStateToProps(state) {
    return state;
}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            login
        },
        dispatch
    );
};

export default connect(mapStateToProps, mapDispatchToProps)(LoginContainer);
