import React, {Component, useEffect} from "react";
import {connect} from "react-redux";
import {bindActionCreators} from "redux";
import {setTranslations} from './redux/lang/actions'
import SessionStorage from "./services/SessionStorage";

let obj = [];

const Translate = props => {

    useEffect(
        () => {
            if (props.name !== undefined){
                let lang = SessionStorage.get('lang') || "am";
                if (obj.length > 0) {
                    let index = false;
                    for (let item of obj) {
                        if (item.value === props.name) {
                            index = true;
                        }
                    }
                    if (index === false) {
                        obj.push({'key': props.name.toLowerCase(), 'name': props.name, 'lnag': lang})
                        // props.setTranslations(props.name, props.name, lang);
                    }
                } else obj.push({'key': props.name.toLowerCase(), 'name': props.name, 'lnag': lang});
                console.log(obj);
            }
        }, []
    )


    // if (!props.translations[props.name.toLowerCase()]) {
    //     if (props.status) {
    //         let lang = SessionStorage.get('lang') || "am";
    //         if (props.translations.length > 0) {
    //             let index = false;
    //             for (let item of props.translations) {
    //                 if (item.value === props.name) {
    //                     index = true;
    //                 }
    //             }
    //             if (index === false) {
    //                 console.log('key: ', props.name.toLowerCase(), 'name: ', props.name, 'lnag: ', lang)
    //                 // props.setTranslations(props.name, props.name, lang);
    //             }
    //         } else {
    //             // props.setTranslations(props.name, props.name, lang);
    //         }
    //     }
    // }


    const contentRender = () => {
        let word = null;
        if (props.name) {
            if (props.translations.length > 0) {
                if (props.translations[(props.name.toLowerCase())]) {
                    console.log("name", props.translations[props.name.toLowerCase()])
                    word = props.translations[props.name.toLowerCase()]
                }
            } else {
                word = props.name
            }
        }
        return word;
    }
    return (
        <span>
                {props.translations[props.name] ? props.translations[props.name] : props.name}
        </span>
    );

}

const mapDispatchToProps = dispatch => {
    return bindActionCreators(
        {
            setTranslations
        },
        dispatch
    );
};

const mapStateToProps = state => {
    return {
        translations: state.languages.translations,
        status: state.languages.status
    }
};

export default connect(mapStateToProps, mapDispatchToProps)(Translate);
